// 1. Imports galleryItems from gallery-items.js file.

import { galleryItems } from './gallery-items.js';
// Change code below this line
// 2. Use querySelector to take access to the <ul class="gallery"></ul> element 
// and stores it in the imagesGallery variable.

const imagesGallery = document.querySelector('.gallery');

// 3. const markupImagesGallery variable stores the markup 
// generated by the createGalleryImages function.

const markupImagesGallery = createGalleryImages(galleryItems);

// 4. inserts the markup stored in the markupImagesGallery variable 
// into the imagesGallery element.

imagesGallery.insertAdjacentHTML("beforeend", markupImagesGallery);

// 5. Calls the createGalleryImages function and passes the imported array 
// of objects as an argument.
// This function creates the markup for each item of the array 
// and returns it as a string.
// Inserts the markup generated by the createGalleryImages function 
// into the imagesGallery element.

function createGalleryImages(items) {
	return items
		.map(({ preview, original, description }) => {
			return `<a class="gallery__item" href="${original}">
						<img class="gallery__image" src="${preview}" data-source="${original}" alt="${description}" />
					</a>`;
		})
		.join(``);
}

// 6. Creates a new SimpleLightbox instance and passes the ".gallery a" selector 
// and an object with two options as arguments.This will allow us to create 
// a lightbox for each image in the gallery.

lightbox = new SimpleLightbox(".gallery a", { captionsData: "alt", captionDelay: 250 });

